INSERT INTO `txp_form` VALUES ('article_listing','article','<li role=\"article\" itemscope itemtype=\"http://schema.org/Article\">\n    <h4 itemprop=\"name headline\">\n        <a href=\"<txp:permlink />\" itemprop=\"url\">\n            <txp:title />\n        </a>\n    </h4>\n\n    <!-- if the article has an excerpt, display that -->\n    <txp:if_excerpt>\n        <div itemprop=\"description\">\n            <txp:excerpt />\n        </div>\n    </txp:if_excerpt>\n\n    <p class=\"footnote\">\n        <txp:text item=\"posted\" />\n        <time datetime=\"<txp:posted format=\"iso8601\" />\" itemprop=\"datePublished\">\n            <txp:posted />\n        </time>\n        ,\n        <txp:text item=\"author\" />\n        <span itemprop=\"author\" itemscope itemtype=\"http://schema.org/Person\">\n            <span itemprop=\"name\">\n                <txp:author link=\"1\" this_section=\"1\" />\n            </span>\n        </span>\n    </p>\n</li>\n','2014-07-23 10:35:19'),('comments','comment','<!-- load the comment email into a variable. you will be using this below along with author email variable loaded in form: default.article.txp\n     then check the comment email variable against article author email variable, and if it matches add \'comments-author\' class -->\n<txp:variable name=\"this_comment\" value=\'<txp:comment_email />\' />\n<txp:if_variable name=\"this_comment\" value=\'<txp:author_email />\'>\n    <article class=\"comments comments-author\" itemscope itemtype=\"http://schema.org/Comment\">\n<txp:else />\n    <article class=\"comments\" itemscope itemtype=\"http://schema.org/Comment\">\n</txp:if_variable>\n\n    <h4>\n        <span class=\"comment-author\" itemprop=\"author\" itemscope itemtype=\"http://schema.org/Person\">\n            <span itemprop=\"name\">\n                <txp:comment_name />\n            </span>\n        </span>\n\n        <!-- ...now check the comment email variable against article author email variable, and if it matches add \'(author)\' text -->\n        <txp:if_variable name=\"this_comment\" value=\'<txp:author_email />\'>\n            <span class=\"is-author\">\n                (<txp:text item=\"author\" />)\n            </span>\n        </txp:if_variable>\n\n        <!-- add a permlink so people can link direct to this comment -->\n        <span class=\"comment-anchor\" itemprop=\"url\">\n            <txp:comment_permlink>#</txp:comment_permlink>\n        </span>\n\n    </h4>\n\n    <!-- also add a \'since\' to show comment freshness -->\n    <p class=\"footnote\">\n        <time datetime=\"<txp:comment_time format=\"iso8601\" />\" itemprop=\"dateCreated\">\n            <txp:comment_time />\n            (<txp:comment_time format=\"since\" />)\n        </time>\n    </p>\n\n    <div itemprop=\"text\">\n        <txp:comment_message />\n    </div>\n\n</article>\n','2014-07-23 10:35:19'),('comments_display','comment','<!-- added an id attribute to the section so we can link directly to here e.g. http://example.com/section/article#comments-head -->\n\n<section id=\"comments-head\" role=\"region\">\n\n    <h3>\n        <txp:text item=\"comments\" />\n    </h3>\n\n    <!-- if there are comments, display them (note: example code below overrides the global preference setting for comments wrapping by stating\n         attributes of wraptag=\"\" and break=\"\", you are instead using ol and li tags below)... -->\n    <txp:if_comments>\n        <ol class=\"comments-list\">\n\n            <txp:comments wraptag=\"\" break=\"li\" /> <!-- links by default to form: \'comments.comment.txp\' unless you specify a different form -->\n\n            <!-- if this is a comment preview, display it (but only if there is no error) -->\n            <txp:if_comments_preview>\n                <li>\n                    <p id=\"cpreview\">\n                        <txp:text item=\"press_preview_then_submit\" />\n                    </p>\n\n                    <!-- links by default to form: \'comments.comment.txp\' unless you specify a different form -->\n                    <txp:comments_preview wraptag=\"\" />\n\n                </li>\n            </txp:if_comments_preview>\n\n        </ol>\n    <txp:else />\n\n        <!-- else if there are no comments and if user is currently previewing comment, display it (but only if there is no error) -->\n        <txp:if_comments_preview>\n            <ol class=\"comments-list\">\n                <li>\n                    <p id=\"cpreview\">\n                        <txp:text item=\"press_preview_then_submit\" />\n                    </p>\n\n                    <!-- links by default to form: \'comments.comment.txp\' unless you specify a different form -->\n                    <txp:comments_preview wraptag=\"\" />\n\n                </li>\n            </ol>\n        <txp:else />\n\n            <!-- else just display that there are simply no comments whatsoever :( ...but only if comments are allowed -->\n            <txp:if_comments_allowed>\n                <p>\n                    <txp:text item=\"no_comments\" />\n                </p>\n            </txp:if_comments_allowed>\n\n        </txp:if_comments_preview>\n\n    </txp:if_comments>\n\n    <!-- if new comments are allowed for this article then display comment form, if not then display \'closed\' messages -->\n    <txp:if_comments_allowed>\n\n        <section id=\"comments-form\" role=\"region\">\n\n            <!-- comment invite text is taken for the article\'s comment invitation field on the \'write\' screen -->\n            <h3>\n                <txp:comments_invite showcount=\"0\" textonly=\"1\" showalways=\"1\" />\n            </h3>\n\n            <!-- links by default to form: \'comment_form.comment.txp\' unless you specify a different form -->\n            <txp:comments_form isize=\"32\" msgcols=\"64\" msgrows=\"4\" />\n\n        </section>\n\n    <txp:else />\n    \n        <!-- display either a comments expired message or a comments disabled message -->\n        <txp:if_comments>\n            <p>\n                <strong>\n                    <txp:text item=\"comments_expired\" />\n                </strong>\n            </p>\n        <txp:else />\n            <p>\n                <strong>\n                    <txp:text item=\"comments_closed\" />\n                </strong>\n            </p>\n        </txp:if_comments>\n\n    </txp:if_comments_allowed>\n\n</section>\n','2014-07-23 10:35:19'),('comment_form','comment','<txp:comments_error wraptag=\"ul\" break=\"li\" />\n\n<p>\n    <txp:text item=\"enter_comment_here\" />\n</p>\n\n<!-- if there is an error, then inform user -->\n<txp:if_comments_error>\n    <txp:comments_error class=\"error_message\" wraptag=\"ol\" break=\"li\" />\n</txp:if_comments_error>\n\n<fieldset>\n\n    <p class=\"large\">\n        <label for=\"name\">\n            <txp:text item=\"comment_name\" />\n            <b class=\"required\" title=\"<txp:text item=\"required\" />\">*</b>\n        </label>\n        <br>\n        <txp:comment_name_input />\n    </p>\n\n    <p class=\"large\">\n        <label for=\"email\">\n            <txp:text item=\"comment_email\" />\n            <b class=\"required\" title=\"<txp:text item=\"required\" />\">*</b>\n        </label>\n        <br>\n        <txp:comment_email_input />\n    </p>\n\n    <p class=\"large\">\n        <label for=\"web\">\n            <txp:text item=\"comment_web\" />\n        </label>\n        <br>\n        <txp:comment_web_input />\n    </p>\n\n    <p>\n        <txp:comment_remember />\n    </p>\n\n    <p class=\"small\">\n        <label for=\"message\">\n            <txp:text item=\"comment_message\" />\n            <b class=\"required\" title=\"<txp:text item=\"required\" />\">*</b>\n        </label>\n        <br>\n        <txp:comment_message_input />\n    </p>\n\n</fieldset>\n\n<!-- preview and submit buttons (note: submit button will have a class of \'disabled\'\n     applied until you have previewed the message at least once) -->\n<p>\n    <txp:comment_preview />\n    <txp:comment_submit />\n</p>\n','2014-07-23 10:35:19'),('default','article','<article role=\"article\" itemscope itemtype=\"http://schema.org/Article\">\n\n    <!-- if not an individual article then make the title h1 a link -->\n    <txp:if_individual_article>\n        <h1 itemprop=\"name headline\">\n            <txp:title />\n        </h1>\n    <txp:else />\n        <h1 itemprop=\"name headline\">\n            <a href=\"<txp:permlink />\" itemprop=\"url\">\n                <txp:title />\n            </a>\n        </h1>\n    </txp:if_individual_article>\n\n    <p>\n        <strong>\n            <txp:text item=\"posted\" />\n        </strong>\n        <time datetime=\"<txp:posted format=\"iso8601\" />\" itemprop=\"datePublished\">\n            <txp:posted />\n        </time>\n        <br>\n        <strong>\n            <txp:text item=\"comments\" />\n        </strong>\n        <a href=\"<txp:permlink />#comments-head\" title=\"<txp:text item=\"view\" />\" itemprop=\"discussionUrl\" itemscope itemtype=\"http://schema.org/UserComments\">\n\n            <!-- if comments then display the number, if no comments then print \'none\' -->\n            <txp:if_comments>\n                <span itemprop=\"interactionCount\"><txp:comments_count /></span>\n            <txp:else />\n                <span itemprop=\"interactionCount\"><txp:text item=\"none\" /></span>\n            </txp:if_comments>\n\n        </a>\n    </p>\n\n    <txp:if_article_image>\n        <txp:images form=\"images\" />\n    </txp:if_article_image>\n\n    <div itemprop=\"articleBody\">\n        <txp:body />\n    </div>\n\n    <p>\n        <strong>\n            <txp:text item=\"author\" />\n        </strong>\n        <span itemprop=\"author\" itemscope itemtype=\"http://schema.org/Person\">\n            <span itemprop=\"name\">\n                <txp:author link=\"1\" this_section=\"1\" />\n            </span>\n        </span>\n\n        <!-- only display categories if they are actually set for an article, otherwise omit -->\n        <txp:if_article_category>\n            <br>\n            <strong>\n                <txp:text item=\"categories\" />\n            </strong>\n            <span itemprop=\"keywords\">\n                <txp:category1 title=\"1\" link=\"1\" />\n                <txp:if_article_category number=\"1\">\n                    <txp:if_article_category number=\"2\">, </txp:if_article_category>\n                </txp:if_article_category>\n                <txp:category2 title=\"1\" link=\"1\" />\n            </span>\n        </txp:if_article_category>\n\n    </p>\n\n    <!-- if this is an individual article then add the comments section via form: comments_display.article.txp -->\n    <txp:if_individual_article>\n        <txp:output_form form=\"comments_display\" />\n    </txp:if_individual_article>\n\n</article>\n','2014-07-23 10:35:19'),('files','file','<!-- set up variables to check whether a file also has a title, description, category associated with it... -->\n<txp:variable name=\"file_download_title\" value=\'<txp:file_download_name title=\"1\" />\' />\n<txp:variable name=\"file_download_description\" value=\'<txp:file_download_description />\' />\n<txp:variable name=\"file_download_category\" value=\'<txp:file_download_category />\' />\n\n<div itemscope itemtype=\"http://schema.org/userDownloads\">\n\n    <!-- ...if exists, use the file title, otherwise use file name -->\n    <txp:if_variable name=\"file_download_title\" value=\"\">\n        <strong itemprop=\"name\"><a href=\"<txp:file_download_link />\" title=\"<txp:file_download_name />\" itemprop=\"url\">\n            <txp:file_download_name /></a>\n        </strong>\n        <br>\n    <txp:else />\n        <strong itemprop=\"name\">\n            <a href=\"<txp:file_download_link />\" title=\"<txp:file_download_name title=\'1\' />\" itemprop=\"url\">\n                <txp:file_download_name title=\"1\" />\n            </a>\n        </strong>\n        <br>\n    </txp:if_variable>\n\n   