andlers = function() {\r\n		var thisObj = this;\r\n		this.$id.click(function(e) {\r\n			thisObj.toggleRegion();\r\n			e.stopPropagation();\r\n			return false;\r\n		});\r\n	}\r\n\r\n	// function toggleRegion() is a member function to toggle the display of the hideShow region\r\n	hideShow.prototype.toggleRegion = function() {\r\n		var thisObj = this;\r\n\r\n		// toggle the region\r\n		this.$region.slideToggle(this.toggleSpeed, function() {\r\n			// region is collapsed\r\n			if ($(this).attr(\'aria-expanded\') === \'false\') { \r\n				// update the aria-expanded attribute of the region\r\n				$(this).attr(\'aria-expanded\', \'true\');\r\n				// update the button state\r\n				thisObj.$id.attr(\'aria-pressed\', \'true\');\r\n			}\r\n			// region is expanded\r\n			else {\r\n				// update the aria-expanded attribute of the region\r\n				$(this).attr(\'aria-expanded\', \'false\');\r\n				// update the button state\r\n				thisObj.$id.attr(\'aria-pressed\', \'false\');\r\n			}\r\n		});\r\n	}\r\n</script>\r\n<script src=\"js/plusone.js\"></script>\r\n	\r\n<!-- Use of head.js to browser & size detection, instead of Modernizr -->\r\n<script src=\"js/head.min.js\"></script>','2014-07-23 12:30:41'),('minimum_footer','misc','<footer role=\"contentinfo\" class=\"span9\">\r\n	<p><small><txp:text item=\"published_with\" /> <a href=\"http://textpattern.com/\" rel=\"external\" title=\"<txp:text item=\'go_txp_com\' />\">Textpattern CMS</a> | Theme: <a href=\"http://minimum.yauh.de/\" rel=\"external\" title=\"Go to the Minimum theme homepage\">Minimum</a> by <a href=\"http://yauh.de\" rel=\"external\" title=\"Go to the theme author’s homepage\">Stephan Hochhaus</a>.</small><p>\r\n</footer>','2014-07-23 10:35:19'),('minimum_drawer','misc','<a id=\"drawer-trigger\" href=\"#\" role=\"button\" aria-controls=\"drawer\" aria-pressed=\"false\">Sobre mí</a>\n\n<aside id=\"drawer\" role=\"complementary\" aria-labelledby=\"drawer-trigger\" tabindex=\"-1\" aria-expanded=\"false\">\n\n	<txp:article_c